    class Solution {

        boolean res = false;

        public boolean exist(char[][] board, String word) {
            if (board == null) {
                return false;
            }
            if (board.length == 0 && word == null) {
                return true;
            }
            if (word == null) {
                return false;
            }

            if (word.length() == 0) {
                return true;
            }


            int x = board.length;
            int y = board[0].length;

            char start = word.charAt(0);

            for (int i = 0; i < x; i++) {
                for (int j = 0; j < y; j++) {
                    char ch = board[i][j];
                    if (ch == start) {

                        board[i][j] = '\0';
                        List res = doCheck(board, i, j, word.substring(1, word.length()));

                        if (res.size() > 0) {
                            return this.res;
                        }

                        board[i][j] = ch;
                    }
                }
            }


            return this.res;
        }

        public List<List<Integer>> doCheck(char[][] board, int i, int j, String word) {

            List<List<Integer>> res = new ArrayList<>();
            int len = word.length();
            if (word.length() == 0) {

                this.res = true;
                List<Integer> preIndex = new ArrayList<>();
                preIndex.add(i);
                preIndex.add(j);
                res.add(preIndex);
                return res;
            }

            char curChar = word.charAt(0);


            if (i > 0 && !this.res) {
                char temp = board[i - 1][j];
                if (temp == curChar) {
                    board[i - 1][j] = '\0';
                    List ch = doCheck(board, i - 1, j, word.substring(1, word.length()));
                    board[i - 1][j] = temp;
                    res.addAll(ch);
                }
            }
            if (j < board[0].length - 1 && !this.res) {
                char temp = board[i][j + 1];
                if (temp == curChar) {
                    board[i][j + 1] = '\0';
                    List ch = doCheck(board, i, j + 1, word.substring(1, word.length()));
                    res.addAll(ch);
                    board[i][j + 1] = temp;
                }
            }
            if (i < board.length - 1 && !this.res) {
                char temp = board[i + 1][j];
                if (temp == curChar) {
                    board[i + 1][j] = '\0';
                    List ch = doCheck(board, i + 1, j, word.substring(1, word.length()));
                    board[i + 1][j] = temp;
                    res.addAll(ch);
                }
            }
            if (j > 0 && !this.res) {
                char temp = board[i][j - 1];
                if (temp == curChar) {
                    board[i][j - 1] = '\0';
                    List ch = doCheck(board, i, j - 1, word.substring(1, word.length()));
                    board[i][j - 1] = temp;
                    res.addAll(ch);
                }
            }

            return res;
        }
    }

    class SolutionA {

        boolean res = false;

        public boolean exist(char[][] board, String word) {
            if (board == null) {
                return false;
            }
            if (board.length == 0 && word == null) {
                return true;
            }
            if (word == null) {
                return false;
            }

            if (word.length() == 0) {
                return true;
            }


            int x = board.length;
            int y = board[0].length;

            char start = word.charAt(0);

            boolean[][] used = new boolean[board.length][board[0].length];

            for (int i = 0; i < x; i++) {
                for (int j = 0; j < y; j++) {
                    char ch = board[i][j];
                    if (ch == start) {

                        if (doCheck(used, board, i, j, word, 0)) {
                            return true;
                        }
                        used[i][j] = false;
                    }
                }
            }


            return false;
        }

        public boolean doCheck(boolean[][] used, char[][] board, int i, int j, String word, int index) {


            if (i >= board.length ||
                    i < 0 ||
                    j >= board[0].length ||
                    j < 0 ||

                    index >= word.length() ||
                    used[i][j] ||

                    board[i][j] != word.charAt(index)) {

                return false;
            }

            if (index == word.length() - 1) {
                return true;
            }

            used[i][j] = true;

            if (doCheck(used, board, i + 1, j, word, index + 1) ||
                    doCheck(used, board, i - 1, j, word, index + 1) ||
                    doCheck(used, board, i, j + 1, word, index + 1) ||
                    doCheck(used, board, i, j - 1, word, index + 1)) {

                return true;
            }

            used[i][j] = false;

            return false;
        }
    }

